mapper.select:
        select sum(students_num-lt60)/sum(students_num) rate
	        from weekly_exam
	        where term_id = #{termId} and week_id = #{weekId}

select e.exam_id,e.course_id,e.classes_id,e.term_id,e.week_id,e.lecturer_id,e.tutor_id,g.grade_id, e.lt60,e.gt60_lt70,e.gt70_lt80,e.gt80_lt90,e.gt90_lt100, e.students_num, lt60/students_num lt60_rate, gt60_lt70/students_num gt60_lt70_rate, gt70_lt80/students_num gt70_lt80_rate, gt80_lt90/students_num gt80_lt90_rate, gt90_lt100/students_num gt90_lt100_rate, (e.students_num-e.lt60)/e.students_num rate, e.is_able,c.course_name,cls.classes_name,t.term_name,w.week_name,l.lecturer_name,tu.tutor_name,g.grade_name from weekly_exam e, course c,classes cls,term t,`week` w,lecturer l, tutor tu, grade g where e.course_id=c.course_id and e.classes_id=cls.classes_id and e.term_id=t.term_id and e.week_id=w.week_id and e.tutor_id=tu.tutor_id and e.lecturer_id=l.lecturer_id and cls.grade_id=g.grade_id
and t.term_id = #{termId}
and w.week_id = #{weekId}
and g.grade_id = #{gradeId}
and cls.classes_id = #{classesId}
and c.course_id = #{courseId}
and tu.tutor_id = #{tutorId}
and l.lecturer_id = #{lecturerId}
and e.is_able = #{isAble}
and t.term_name like '%${termName}%'
and w.week_name like '%${weekName}%'
and g.grade_name like '%${gradeName}%'
and cls.classes_name like '%${classesName}%'
and c.course_name like '%${courseName}%'
and tu.tutor_name like '%${tutorName}%'
and l.lecturer_name like '%${lecturerName}%'
order by e.exam_id desc
